import { useState } from "react";

export default function Main() {
    const [hasStarted, setHasStarted] = useState(false);

    function startQuiz() {
        setHasStarted((prev) => !prev);
    }

    return (
        <div>
            {!hasStarted && (
                <div>
                    <h1>Quizzical</h1>
                    <p>Some description if needed</p>
                    <button className="start" onClick={startQuiz}>
                        Start quiz
                    </button>
                </div>
            )}
        </div>
    );
}
