export default function Question(props) {
    const data = props.data;

    const answers = [...data.incorrect_answers, data.correct_answer];
    const answersElements = answers.map((answer) => {
        return answer === data.correct_answer ? (
            <div
                className="correct anwser"
                dangerouslySetInnerHTML={{ __html: answer }}
            ></div>
        ) : (
            <div className="anwser" dangerouslySetInnerHTML={{ __html: answer }}></div>
        );
    });

    return (
        <div>
            <h2 dangerouslySetInnerHTML={{ __html: data.question }}></h2>
            {answersElements}
        </div>
    );
}
