import { nanoid } from "nanoid";
import { useEffect, useState } from "react";

export default function Main() {
    const [hasStarted, setHasStarted] = useState(true);
    const [questions, setQuestions] = useState([]);

    useEffect(() => {
        fetch("https://opentdb.com/api.php?amount=5&category=9&difficulty=medium")
            .then((res) => res.json())
            .then((data) => setQuestions(data.results));
    }, []);

    function startQuiz() {
        setHasStarted((prev) => !prev);
    }

    const questionElements = questions.map((obj) => {
        let anwsers = [
            { question: obj.correct_answer, correct: true },
            { question: obj.incorrect_answers[0], correct: false },
            { question: obj.incorrect_answers[1], correct: false },
            { question: obj.incorrect_answers[3], correct: false },
        ];
        const shuffledArray = anwsers.sort((a, b) => 0.5 - Math.random());

        console.log(anwsers);

        return (
            <h2 key={nanoid()} dangerouslySetInnerHTML={{ __html: obj.question }}></h2>
        );
    });

    return (
        <div>
            {/* start screen */}
            {!hasStarted && (
                <div>
                    <h1>Quizzical</h1>
                    <p>Some description if needed</p>
                    <button className="start" onClick={startQuiz}>
                        Start quiz
                    </button>
                </div>
            )}
            {/* quiz screen */}
            {hasStarted && <div>{questionElements}</div>}

            {/* Background */}
        </div>
    );
}
