import { nanoid } from "nanoid";
import { useEffect, useState } from "react";
import Question from "./Question";
import blobs from "../images/blobs.png";
import blobs2 from "../images/blobs2.png";

export default function Main() {
    const [hasStarted, setHasStarted] = useState(false);
    const [questions, setQuestions] = useState([]);

    useEffect(() => {
        fetch("https://opentdb.com/api.php?amount=5&category=9&difficulty=medium")
            .then((res) => res.json())
            .then((data) =>
                setQuestions(() => {
                    const result = data.result;
                    return {
                        question: result.question,
                        answers: [...result.incorrect_answers, result.correct_answers],
                        correct_answers: result.correct_answers,
                        selected: "",
                    };
                })
            );
    }, []);

    function startQuiz() {
        setHasStarted((prev) => !prev);
    }

    const questionElements = questions.map((obj) => {
        const id = nanoid();
        return <Question data={obj} key={id} id={id} />;
    });

    return (
        <div>
            {/* start screen */}
            {!hasStarted && (
                <div className="start--container">
                    <h1>Quizzical</h1>
                    <p>Some description if needed</p>
                    <button className="start" onClick={startQuiz}>
                        Start quiz
                    </button>
                </div>
            )}
            {/* quiz screen */}
            {hasStarted && (
                <div>
                    <div>{questionElements}</div>
                    <button>Check answers</button>
                </div>
            )}

            {/* Background */}
            <img src={blobs} id="blobs" alt=""></img>
            <img src={blobs2} id="blobs2" alt=""></img>
        </div>
    );
}
