export default function Question(props) {
    const data = props.data;

    const anwsers = [...data.incorrect_answers, data.correct_anwser];
    const anwsersElements = anwsers.map((anwser) => {
        return <div className="anwser">{anwser}</div>;
    });

    return (
        <div>
            <h2 dangerouslySetInnerHTML={{ __html: data.question }}></h2>
            {anwsersElements}
        </div>
    );
}
