import { nanoid } from "nanoid";

export default function Question(props) {
    const data = props.data;

    const answers = [...data.incorrect_answers, data.correct_answer];
    const shuffledArray = answers.sort(() => Math.random() - 0.5);

    const answersElements = shuffledArray.map((answer) => {
        return (
            <div key={nanoid()}>
                <input
                    type="radio"
                    className="answer"
                    value={answer}
                    name={data.id}
                    onChange={() => props.setSelected(data.id, answer)}
                ></input>
                <label dangerouslySetInnerHTML={{ __html: answer }}></label>
            </div>
        );
    });

    return (
        <div>
            <h2 dangerouslySetInnerHTML={{ __html: data.question }}></h2>
            <div>{answersElements}</div>
        </div>
    );
}
