export default function Settings() {
    return (
        <div className="settings">
            <div className="slidecontainer">
                <input
                    type="range"
                    min="1"
                    max="10"
                    value={questionAmount}
                    onChange={handleChange}
                    className="slider"
                    id="myRange"
                ></input>
            </div>
            <h4>Amount of questions: {questionAmount}</h4>
        </div>
    );
}
